import classes from "./ShortList.module.css"
import { useState } from "react";

function ShortList () {

    const [task, setTask] = useState("");
    const [list, setList] =useState([
        {id: 0,
        title: "sent 20 messages in"},
    ]);

    const deleteHandler = (index) => {
    const newList = [...list];
    newList.splice(index, 1);
    setList(newList);
    };

    const addNewTask = (e) =>{
    e.preventDefault();
    const newTask = {
        id: e.target.task.index
    }
    }




    return (
        <div className={classes.shortList_container}>
            <h3>Short List</h3>
            <ul>
                {
                    list.map((element, index) => 
                    <div key={index} className={classes.task_wrap}>
                    <li>{element.title}</li>
                    <button onClick={(index)=>{deleteHandler(index)}} className={classes.deleteBtn}>x</button>
                 </div>
                 )}
            </ul>
            <form onSubmit={addNewTask}>
            <input value={task} onChange={(e) => setTask(e.target.value)}></input>
            <button>Add</button>
            </form>
        </div>
    )
};

export default ShortList;
